mapper.select:

select 

from ATOMIC_TRANSFER_LOG
where TIME = #{time,jdbcType=VARCHAR}
  and STATUS = #{status,jdbcType=DECIMAL}
  and COMMAND_ID = #{commandId,jdbcType=VARCHAR}

mapper.delete:

delete from ATOMIC_TRANSFER_LOG
where TIME = #{time,jdbcType=VARCHAR}
  and STATUS = #{status,jdbcType=DECIMAL}
  and COMMAND_ID = #{commandId,jdbcType=VARCHAR}
