mapper.select:
select CL.carrier_id, DECODE( tsc.comm_state, 'NotCommunicating', 'NotAvailable', event_carrier.host_carrier_state ) as comm_state, CL.carrier_state, CL.current_tsc_id, tsc.tsc_abbreviation as tscid, CL.current_loc, CL.current_zone_id, CL.stored_time, CL.BLOCKstr, CL.insystem_carrier_id, CL.insystem_time, CL.insystem_location, carrier_tag.next_dst as next_dst, CL.priority, CL.offlinestr from ( select rn,carrier_id,carrier_state,current_tsc_id,current_loc,current_zone_id,stored_time,BLOCKstr,insystem_carrier_id,insystem_time,insystem_location,priority,offlinestr from ( select ROW_NUMBER() over(order by CARRIER_ID asc) rn, carrier.carrier_id as carrier_id, carrier.carrier_state as carrier_state, carrier.current_tsc_id as current_tsc_id, carrier.current_loc as current_loc, carrier.current_zone_id as current_zone_id, to_char(to_timestamp(carrier.stored_time,'yyyymmddhh24missff'),'YYYY/MM/DD HH24:MI:SS') as stored_time, CASE WHEN carrier.block_flg=0 AND carrier.prohibit_flg=0 THEN 'Normal' WHEN carrier.block_flg=0 AND carrier.prohibit_flg=1 THEN 'Prohibit' ELSE 'Block' END as BLOCKstr, carrier.insystem_carrier_id as insystem_carrier_id, to_char(to_timestamp(carrier.insystem_time,'yyyymmddhh24missff'),'YYYY/MM/DD HH24:MI:SS') as insystem_time, carrier.insystem_location as insystem_location, carrier.priority as priority, CASE carrier.offline_flg WHEN 0 THEN 'Online' WHEN 1 THEN 'Offline' END as offlinestr from CARRIER
)
) CL LEFT OUTER JOIN carrier_tag ON carrier_tag.carrier_id = CL.carrier_id LEFT OUTER JOIN event_carrier ON event_carrier.carrier_id = CL.carrier_id LEFT OUTER JOIN tsc ON tsc.tsc_id = CL.current_tsc_id order by CL.CARRIER_ID asc

select rownum as no, t.GroupId
from (
  SELECT port_grp_id as GroupId
  FROM   port_grp_rlt 
  WHERE  port_id = #{portId} 
  UNION 
  SELECT ohb_id as GroupId
  FROM   ohb_port_rlt 
  WHERE  port_id = #{portId}
) t
