mapper.select:

select 

from MACRO_TRANSFER_LOG
where TIME = #{time,jdbcType=VARCHAR}
  and STATUS = #{status,jdbcType=DECIMAL}
  and ORG_COMMAND_ID = #{orgCommandId,jdbcType=VARCHAR}
  and ORG_CARRIER_ID = #{orgCarrierId,jdbcType=VARCHAR}

mapper.delete:

delete from MACRO_TRANSFER_LOG
where TIME = #{time,jdbcType=VARCHAR}
  and STATUS = #{status,jdbcType=DECIMAL}
  and ORG_COMMAND_ID = #{orgCommandId,jdbcType=VARCHAR}
  and ORG_CARRIER_ID = #{orgCarrierId,jdbcType=VARCHAR}
